class Solution {
public:
    int maximumRequests(int n, vector<vector<int>>& requests) {
        vector<unordered_map<int, int>> building(n, unordered_map<int, int>());
        for (auto& request : requests) {
            int from = request[0];
            int to = request[1];

            ++building[from][to];
        }

        int ans = 0;
        for (int i = 0; i < n; ++i) {
            bool isInLoop = true;
            do {
                vector<int> buffer;
                unordered_set<int> visited;
                vector<int> longestStack;
                dfs(building, i, buffer, visited, longestStack);
                if (longestStack.empty()) {
                    isInLoop = false;
                } else {
                    ans += longestStack.size();
                    for (int i = 1; i <= longestStack.size(); ++i) {
                        --building[longestStack[i - 1]][longestStack[i % longestStack.size()]];
                    }
                }
            } while (isInLoop);
        }

        return ans;
    }

private:
    void dfs(vector<unordered_map<int, int>>& building, int i, vector<int>& buffer,
             unordered_set<int>& visited, vector<int>& longest) {
        if (visited.count(i)) {
            vector<int> circle;
            bool head = false;
            for (int j = 0; j < buffer.size(); ++j) {
                if (buffer[j] == i) {
                    head = true;
                }
                if (head == true) {
                    circle.emplace_back(buffer[j]);
                }
            }
            if (circle.size() > longest.size()) {
                longest = move(circle);
            }
            return;
        }

        visited.emplace(i);
        buffer.emplace_back(i);
        for (auto [neighbor, num] : building[i]) {
            if (num > 0) {
                dfs(building, neighbor, buffer, visited, longest);
            }
        }
        buffer.pop_back();
        visited.erase(i);
    }
};
