# Code
```
class Solution {
public:
    int maximumRequests(int n, vector<vector<int>>& requests) {

        int len=requests.size();

        vector<vector<int>> a,b;
        for(int i=0; i<len/2; i++)a.push_back(requests[i]);
        for(int i=len/2; i<len; i++)b.push_back(requests[i]);

        map<vector<int>,int> hsh;

        int ans=0;
        bool flag;

        len=a.size();
        for(int i=1; i<(1<<len); i++)
        {
            vector<int> net(n);
            for(int j=0; j<len; j++)
            {
                if((1<<j)&i)
                {
                    net[a[j][0]]--;
                    net[a[j][1]]++;
                }
            }
            flag=true;
            for(int j=0; j<n; j++)
            {
                if(net[j]!=0)flag=false;
            }
            if(flag)ans=max(ans,__builtin_popcount(i));
            hsh[net]=max(hsh[net],__builtin_popcount(i));
        }

        len=b.size();
        for(int i=1; i<(1<<len); i++)
        {
            vector<int> net(n);
            for(int j=0; j<len; j++)
            {
                if((1<<j)&i)
                {
                    net[b[j][0]]++;
                    net[b[j][1]]--;
                }
            }
            flag=true;
            for(int j=0; j<n; j++)
            {
                if(net[j]!=0)flag=false;
            }
            if(flag)ans=max(ans,__builtin_popcount(i));
            if(hsh.count(net))ans=max(ans,__builtin_popcount(i)+hsh[net]);
        }   
        return ans;
    }
};
```

