class Solution {

    class Edge {
        int v, w, sw, rev;
        public Edge(int v, int w, int sw, int rev) {
            this.v = v;
            this.w = w;
            this.sw = sw;
            this.rev = rev;
        }
    }

    public int maximumRequests(int n, int[][] requests) {
        ArrayList<Edge>[] g = new ArrayList[n];
        for (int i = 0; i < n; i++) g[i]=new ArrayList<>();

        int ans = 0;
        for (int[] e : requests) {
            int u = e[0], v = e[1];
            if (u == v) {
                ans++;
                continue;
            }
            g[u].add(new Edge(v, 1, 1, g[v].size()));
            g[v].add(new Edge(u, -1, 0, g[u].size()-1));
        }

        while (true) {
            int[] val=new int[n], pv=new int[n], pe=new int[n];
            int last=0;
            for(int c=0;c<=n&&last>=0;c++) {
                last=-1;
                for(int i=0;i<n;i++) {
                    int v=val[i];
                    for(int j=0;j<g[i].size();j++) {
                        Edge e=g[i].get(j);
                        if(e.sw==0||val[e.v]>=v+e.w) continue;
                        val[e.v]=v+e.w;
                        pv[e.v]=i;
                        pe[e.v]=j;
                        last=e.v;
                    }
                }
                if(last==-1) return ans;
            }
            boolean[] loop=new boolean[n];
            while(!loop[last]) {
                loop[last]=true;
                last=pv[last];
            }

            int u = last;
            do {
                int p = pv[u], i = pe[u];
                ans += g[p].get(i).w;
                g[p].get(i).sw = 0;
                g[u].get(g[p].get(i).rev).sw = 1;
                u = p;
            } while(u != last);
        }
    }
}
