class Solution {
    void dfs(int i, int m, int cnt, vector<int> &v, vector<vector<int>> &req, int &ans) {
        if(i == m) {
            int f = 0;
            for(int i : v) {
                if(i) {
                    f = 1;
                    break;
                }
            }
            if(!f) ans = max(ans, cnt);
            return;
        }

        dfs(i + 1, m, cnt, v, req, ans);

        v[req[i][0]]--;
        v[req[i][1]]++;
        dfs(i + 1, m, cnt + 1, v, req, ans);
        v[req[i][0]]++;
        v[req[i][1]]--;
    }
public:
    int maximumRequests(int n, vector<vector<int>>& requests) {
        int ans = 0, m = requests.size();
        vector<int> v(n);
        dfs(0, m, 0, v, requests, ans);
        return ans;
    }
};
