# Code
```
public class Solution {

    int MaxCount;

    public bool IsValid(int[] transfers){
        return (transfers.Min() >= 0);
    }

    public void Traverse(int index, int count, int[] transfers, int[][] requests){

        if (IsValid(transfers)){
            this.MaxCount = Math.Max(count, MaxCount);
        }

        if (index == requests.Length){
            return;
        }

        int from = requests[index][0];
        int to = requests[index][1];

        transfers[from]--;
        transfers[to]++;

        Traverse(index + 1, count + 1, (int[]) transfers.Clone(), requests);

        transfers[from]++;
        transfers[to]--;

        Traverse(index + 1, count, (int[]) transfers.Clone(), requests);
    }


    public int MaximumRequests(int n, int[][] requests) {
        int[] transfers = new int[n];
        this.MaxCount = 0;
        Traverse(0, 0, transfers, requests);
        return this.MaxCount;
    }
}
