class Solution {
    public int maximumRequests(int n, int[][] requests) {
        int len = requests.length;
        HashSet<Integer>set = new HashSet<>();
        int ans = answer(n,len,requests,set,0);
        return ans;
    }
    public int answer(int n,int len,int[][] requests,HashSet<Integer>set,int i){
        if(i==len){
            if(isFeasible(n,len,requests,set)){
                return set.size();
            }
            else{
                return 0;
            }
        }
        set.add(i);
        int ans1 = answer(n,len,requests,set,i+1);
        set.remove(i);
        int ans2 = answer(n,len,requests,set,i+1);
        return Math.max(ans1,ans2);
    }
    public boolean isFeasible(int n,int len,int requests[][],HashSet<Integer>set){
        int indegree[] = new int[n];
        for(int i = 0;i < len;i++){
            if(set.contains(i)){
                indegree[requests[i][1]]++;
                indegree[requests[i][0]]--;
            }
        }
        for(int i = 0;i < n;i++){
            if(indegree[i]!=0){
                return false;
            }
        }
        return true;
    }
}
```

