        int[] currReq = reqs[index];
        --freq[currReq[0]]; ++freq[currReq[1]];
        backtrack(reqs, freq, index + 1, count + 1);
        ++freq[currReq[0]]; --freq[currReq[1]];
        backtrack(reqs, freq, index + 1, count);

    }
}
