function solve(idx,req,store,n){
    if(idx>=req.length){
        let cnt= Array(n).fill(0);
        let t=0;
        for(let i=0;i<idx;i++){
            if((store & (1<<i))!==0){
                cnt[req[i][0]]--;
                cnt[req[i][1]]++;
                t++;
            }
        }
        for(let ele of cnt) if(ele!==0) return 0;
        return t;
    }
    let ans=0;
    let temp = store | (1<<idx);
    ans= Math.max(solve(idx+1,req,temp,n),ans); 
    ans =Math.max(solve(idx+1,req,store,n),ans);
    return ans;
}
var maximumRequests = function(n, req) {

    return solve(0,req,0,n);
};
