class Solution {
public:
    int solve(vector<int> &temp,int i,vector<vector<int>> &req){
        // If all requests are over then check net change
        if(i == req.size()){
            // If net change of all building not equal to zero then return INT_MIN
            for(auto no:temp) if(no!=0) return INT_MIN;
            // If it is equal to zero then return 0
            return 0;
        }
        // Checking all the possiblities
        // Update all the changes in temp for ith request
        temp[req[i][0]]--;
        temp[req[i][1]]++;
        // case 1: If we include the request 
        int ans1 = 1 + solve(temp,i+1,req);
        temp[req[i][0]]++;
        temp[req[i][1]]--;
        // case 2: If we reject the request
        int ans2 = solve(temp,i+1,req);
        return max(ans1,ans2);
    }
    int maximumRequests(int n, vector<vector<int>>& requests) {
        // this vector denotes net change of all buildings
        vector<int> temp(n,0);
        return solve(temp,0,requests);
    }
};
```

