public class Solution {
    public int MaximumRequests(int n, int[][] requests) {
        int result = 0;
        int len = requests.Length;

        for (int i = 0; i < (1 << len); i++)
        {
            int[] inDegree = new int[n];
            int pos = len - 1;
            int bitCount = BitOperations.PopCount((uint)i);

            if (bitCount > result)
            {
                for (int cur = i; cur > 0; pos--)
                {
                    if ((cur & 1) > 0)
                    {
                        inDegree[requests[pos][0]]--;
                        inDegree[requests[pos][1]]++;
                    }

                    cur = cur >> 1;
                }

                bool flag = true;
                for (int j = 0; j < n; j++)
                {
                    if (inDegree[j] > 0)
                    {
                        flag = false;
                        break;
                    }
                }

                if (flag)
                {
                    result = bitCount;
                }
            }
        }

        return result;
    }
}
```

