class Solution {
public:
    bool isValidRequests(vector<int> &shifting)
    {
        for(int i=0;i<shifting.size();i++)
        {
            if(shifting[i] != 0)
            {
                return false;
            }
        }
        return true;
    }
    void solve(int idx,int reqctr, vector<vector<int>> &requests, vector<int> &shifting, int &ans)
    {
        if(idx == requests.size())
        {
            if(isValidRequests(shifting))
            {
                ans = max(ans, reqctr);
            }
            return;
        }

        solve(idx+1, reqctr, requests, shifting, ans);

        shifting[requests[idx][0]]--;
        shifting[requests[idx][1]]++;
        solve(idx+1, reqctr+1, requests, shifting, ans);
        shifting[requests[idx][0]]++;
        shifting[requests[idx][1]]--;
    }
    int maximumRequests(int n, vector<vector<int>>& requests) {
        vector<int> shifting(n, 0);
        int ans = 0;
        solve(0, 0, requests, shifting, ans);
        return ans;
    }
};
