            ans = Math.max(ans, temp);
            return;
        }

        int i = req[ind][0];
        int j = req[ind][1];

//         Permit the request at index ind
        degree[i] = degree[i]-1;
        degree[j] = degree[j]+1;
        permitReq(req, ind+1, degree, temp+1);

//         Reject the request at index ind
        degree[i] = degree[i]+1;
        degree[j] = degree[j]-1;
        permitReq(req, ind+1, degree, temp);
    }
}
