class Solution {
public:

    void fun(vector < vector < int > > & rq , vector < int > & cap , int ind , int req , int& maxRequests){ 

        if(ind >= rq.size()){ 

            for(auto a : cap){ 
              if( a != 0) return; // the capacity of each building should be zero if it is not then it will be unbalanced request
			  //hence answer cant be find and we will return 
            }
            maxRequests = max(maxRequests , req); 
            return; 
        }


        fun(rq , cap , ind+1 , req , maxRequests);  // if we are not considering current requests

        cap[rq[ind][0]]--; cap[rq[ind][1]]++;   // if any request enter in a building then the capacity increase by 1 and if leave from the building then capacity decrease by 1

      fun(rq , cap , ind+1 , req+1 , maxRequests);  // if we are considering the current request

         cap[rq[ind][0]]++; cap[rq[ind][1]]--;   // backtrack the requests

    }

    int maximumRequests(int n, vector<vector<int>>& requests) {
       int maxRequests = 0 ; 
        vector < int > capacity(n , 0);  //number of requests each buildings holds
        fun(requests , capacity , 0 , 0 , maxRequests); 
        return maxRequests; 
    }
};
