class Solution {
    public int maximumRequests(int n, int[][] requests) {

        int ans = 0;
        int totalComb = (1 << requests.length);
        for(int i=0; i<totalComb; i++){
            int[] totalInOut = new int[n];

            for(int j=0; j<16; j++){
                if((i & (1<<j)) == 0) continue;

                totalInOut[requests[j][0]]--;
                totalInOut[requests[j][1]]++;
            }

            boolean requestsPossible = true;
            for(int buildingNum=0; buildingNum < n; buildingNum++){
                if(totalInOut[buildingNum] != 0){
                    requestsPossible = false;
                    break;
                }
            }

            /*
                If the requests in the current combination are possible, find the total number of requests serviced in the current combination
                (i.e. the number of 1 bits)
            */
            if(requestsPossible){
                ans = Math.max(ans, Integer.bitCount(i));
            }
        }


        return ans;
    }
}
