from functools import lru_cache

class Solution:
    def maximumRequests(self, n: int, requests: List[List[int]]) -> int:
        m = len(requests)
        @lru_cache(None)
        def dp(i, degree):
            if i == m: return 0 if all(k == 0 for k in degree) else float('-inf')
            if requests[i][0] == requests[i][1]: return 1 + dp(i + 1, degree)
            ret = dp(i + 1, degree)
            nxt_degree = list(degree)
            nxt_degree[requests[i][0]] -= 1
            nxt_degree[requests[i][1]] += 1
            ret = max(ret, 1 + dp(i + 1, tuple(nxt_degree)))
            return ret
        return max(0, dp(0, tuple([0] * n)))
