class Solution {
    public int maximumRequests(int n, int[][] requests) {
        return dfs(new int[n], 0, 0, requests, 0);
    }

    private int dfs(int[] cnts, int unbalanced, int cur, int[][] requests, int cnt) {
        if (cur >= requests.length) {
            return unbalanced == 0 ? cnt : 0;
        }
        int max = 0;
        max = Math.max(max, dfs(cnts, unbalanced, cur + 1, requests, cnt));
        int[] r = requests[cur];
        --cnts[r[0]];
        unbalanced += cnts[r[0]] == -1 ? 1 : (cnts[r[0]] == 0 ? -1 : 0);
        ++cnts[r[1]];
        unbalanced += cnts[r[1]] == 1 ? 1 : (cnts[r[1]] == 0 ? -1 : 0);
        max = Math.max(max, dfs(cnts, unbalanced, cur + 1, requests, cnt + 1));
        --cnts[r[1]];
        ++cnts[r[0]];
        return max;
    }
}
