        int ans = 0;
        for(int i = 1; i < (1 << alpha); ++i) {
            vector<int> move(n);
            for(int j = 0; j <= 15; ++j) {
                if((1 << j) & i) {
                    move[requests[j][0]]++;
                    move[requests[j][1]]--;
