    private void dfs(int curIdx, int[][] requests, int handled) {
        if(curIdx == requests.length) {
			if(res > handled) return ;
            for(int i = 0; i < cnt.length; i++) if(cnt[i] != 0) return ;

            res = Math.max(res, handled);
            return ;
