        // handle
        cnt[requests[curIdx][0]]--;
        cnt[requests[curIdx][1]]++;
        dfs(curIdx + 1, requests, handled + 1);
        cnt[requests[curIdx][0]]++;
        cnt[requests[curIdx][1]]--;

        dfs(curIdx + 1, requests, handled);
    }
}
