class Solution {
    int ans = 0;
    public int maximumRequests(int n, int[][] req) {
        bt(n, 0, req, new ArrayList<>());
        return ans;
    }

    void bt(int n, int idx, int[][] req, List<int[]> cur) {
        if (idx == req.length) {
            int[] deg = new int[n];
            for (int[] r : cur) {
                deg[r[0]]--;
                deg[r[1]]++;
            }
            for (int i = 0; i < n; i++) {
                if (deg[i] != 0) {
                    return;
                }
            }
            ans = Math.max(ans, cur.size());
            return;
        }
        cur.add(req[idx]);
        bt(n, idx + 1, req, cur);
        cur.remove(cur.size() - 1);
        bt(n, idx + 1, req, cur);
    }
}
