class Solution {
    // Define a constant for the modulo
    final int MOD = 1000000007;
    // Define a variable to store the maximum number of requests
    int maxRequests = 0;

    public int maximumRequests(int n, int[][] requests) {
        // Call a helper method to find the maximum number of requests using backtracking
        backtrack(requests, 0, new int[n], 0);
        // Return the maximum number of requests
        return maxRequests;
    }

    // Helper method to find the maximum number of requests using backtracking
    private void backtrack(int[][] requests, int index, int[] balance, int count) {
        // If we have reached the end of the requests array, check if the balance is zero for all buildings
        if (index == requests.length) {
            for (int b : balance) {
                if (b != 0) return; // If not, return without updating the maximum
            }
            // Update the maximum number of requests with the current count
            maxRequests = Math.max(maxRequests, count);
            return;
        }
        // Get the from and to buildings of the current request
        int from = requests[index][0];
        int to = requests[index][1];
        // Option 1: Choose the current request and update the balance and count accordingly
        balance[from]--;
        balance[to]++;
        backtrack(requests, index + 1, balance, count + 1);
        balance[from]++; // Backtrack
        balance[to]--;
        // Option 2: Skip the current request and keep the balance and count unchanged
        backtrack(requests, index + 1, balance, count);
    }
}

