class Solution {
    public int ma=0;
    public void solve(int req[][],int arr[],int n,int idx,int accepted){
        if(idx==req.length){
            if(isValid(arr))
                ma=Math.max(ma,accepted);
            return;
        }
        arr[req[idx][0]]--;
        arr[req[idx][1]]++;
        solve(req,arr,n,idx+1,accepted+1);
        arr[req[idx][0]]++;
        arr[req[idx][1]]--;
        solve(req,arr,n,idx+1,accepted);
