class Solution {

    int achiReq = 0;

    public int maximumRequests(int n, int[][] requests) {

        int[][] grid = new int[n][n];
        int[][] visited = new int[n][2];

        for(int[] req : requests){
            grid[req[0]][req[1]] += 1;
        }

        for(int i = 0; i < n; i++){
            dfs(grid, i, visited, 0);
        }

        return achiReq;
    }

    public void dfs(int[][] grid, int i, int[][] visited, int reqCount){

        visited[i][0] = 1;
        visited[i][1] = reqCount;

        for(int j = 0; j < grid.length; j++){
            if(grid[i][j] > 0){
                grid[i][j] -= 1;
                if(visited[j][0] == 1 && reqCount + 1 - visited[j][1] >= 0){
                    achiReq += reqCount + 1 - visited[j][1];
                    dfs(grid, j, visited, visited[j][1]);
                } else {
                    dfs(grid, j, visited, reqCount + 1);
                }
            }
        }

        visited[i][0] = 0;
    }
}

