class Solution {
    public String mostCommonWord(String paragraph, String[] banned) {
        String[] p = paragraph.replaceAll("!", "")
            .replaceAll("\\?", " ")
            .replaceAll("'", " ")
            .replaceAll(",", " ")
            .replaceAll(";", " ")
            .replaceAll("\\.", " ")
            .toLowerCase()
            .replaceAll("\\s+", " ")
            .split(" ");

        Map<String, Integer> map = new HashMap();
        for (String s : p)
            map.put(s, map.getOrDefault(s, 0) + 1);

        for (String s : banned)
            if (map.containsKey(s))
                map.remove(s);

        int max = 0;
        String s = "";

        for (String k : map.keySet()) {
            if (map.get(k) > max) {
                max = map.get(k);
                s = k;
            }
        }

        return s;
    }
}
