public String mostCommonWord(String paragraph, String[] banned) {
	var wordToCount = new HashMap<String, Integer>();
	var maxCount = 0;
	var mostCommon = "";
	var bannedSet = new HashSet<String>();
	Collections.addAll(bannedSet, banned);

	for (var word : paragraph.toLowerCase().split("[!?;.,' ]")) {
		if (word.isEmpty() || bannedSet.contains(word))
			continue;
		var count = wordToCount.compute(word, (k, v) -> (v == null) ? 1 : ++v);
		if (count > maxCount) {
			maxCount = count;
			mostCommon = word;
		}
	}
	return mostCommon;
}
