func mostCommonWord(para string, banned []string) string {

    // create a map of banned words
    bannedStrings := make(map[string]int)
    for _, val := range banned {
        bannedStrings[val] = 1
    }

    mostCommonWord := ""
    mostCommonWordFreq := 0

    var sb strings.Builder
    words := make(map[string]int)
    for i, val := range para {
        if unicode.IsLetter(val) {
            sb.WriteRune(unicode.ToLower(val))
            if i != len(para) - 1 {
                continue
            }
        }

        // Found a word!
        w := sb.String()
        if w == "" {
            continue
        }
        // Reset string builder
        sb.Reset()

        // For each word found in para, check of it is present in the banned word, if yes, continue
        _, ok := bannedStrings[w]
        if ok {
            continue
        }

        // Add the word, if present update the frequnecy
        _, ok = words[w]
        if ok {
            words[w] += 1
        } else {
            words[w] = 1
        }

        // Update the answer if required
         if (words[w] > mostCommonWordFreq) {
            mostCommonWordFreq = words[w]
            mostCommonWord = w
         }

    }

    return mostCommonWord
}
