class Solution {
public:
    void skipSpace(string &paragraph, int& j, int& n) {
        while(paragraph[j] == ' ')
                j++;
    }

    void moveIndexTillValid(string &paragraph, int& j, int& n, string &temp) {
        while(j < n && (paragraph[j] >= 'a' && paragraph[j] <= 'z')) {
            temp.push_back(paragraph[j]);
            j++;
        }
    }

    string mostCommonWord(string paragraph, vector<string>& banned) {
        transform(begin(paragraph), end(paragraph), begin(paragraph), ::tolower);

        unordered_set<string> st(begin(banned), end(banned));

        unordered_map<string, int> mp;

        int n = paragraph.length();
        int j;
        string temp = "";
        for(int i = 0; i<n;) {
            temp = "";
            j = i;

            skipSpace(paragraph, j, n);
            moveIndexTillValid(paragraph, j, n, temp);

            mp[temp]++;
            i = j+1;
        }

        string result = "";
        int maxFreq = 0;
        for(auto &it : mp) {
            if(st.find(it.first) != st.end()) //skip banned words
                continue;

            if(it.second > maxFreq) {
                result = it.first;
                maxFreq = it.second;
            }

        }
