        unordered_map<char, int> str_freq, txt_freq;
        for (auto s : str) // Storing occurence of each letter of str in str_freq 
            str_freq[s]++;
        for (auto s : text)  // Storing occurence of each letter of text in txt_freq 
            txt_freq[s]++;
        int prev = txt_freq['b']/str_freq['b']; // no. of b in txt_freq / no. of b in str_freq
        for (auto e : str_freq) {
            int curr = txt_freq[e.first]/e.second; // finding no. of each letter in txt_freq / no. of that in str_freq 
            prev = min(prev, curr); // minimum of the prev and current 
        }
        return prev;
    }
