class Solution {
public:
    int maxNumberOfBalloons(string text) {

        //time complexity - O(n)
        //space complexity - O(1)

        // find the frequency for all characters in balloon string - constant time opertaion
        vector<int> balloonFreq(26,0);
        string target = "balloon";
        for(int i=0; i <target.size(); i++) {
            balloonFreq[target[i]-'a']++;
        }

        // find the frequency for all characters in text string
        vector<int> textFreq(26, 0);
         for(int i=0; i <text.size(); i++) {
            textFreq[text[i]-'a']++;
        }

        //find factor the divisiblity factor of the frequency of character in  text with that of balloon        

        vector<int> factorFreq(26, 0);
        int minFactor = INT_MAX;
        for(int i=0; i <factorFreq.size(); i++) {
            // case where the text string does not contain the frequency to even form balloon single time
            if(textFreq[i]==0 && balloonFreq[i]!=0) return 0;

            // case - text = bbaallloon =>
            //a freq 2 which means we can have max balloon string 2 times in text
             //b freq 2,  which means we can have max balloon string 2 times in text
            //l freq 3, the in target string l freq 2 , which means we now have max 1 chance to form balloon target.
            else if(textFreq[i]!=0 && balloonFreq[i]!=0) {
                factorFreq[i] = textFreq[i]/balloonFreq[i];
                minFactor = min(minFactor, factorFreq[i]);
            }
        }

        return minFactor;

    }
};
```

