class Solution {
    public int maxNumberOfBalloons(String text) {
        if(text.length()<7) return 0;
        HashMap<Character,Integer> hp = new HashMap();
        ArrayList<Character> al = new ArrayList(Arrays.asList('b','a','l','l','o','o','n'));

        for(int i=0;i<text.length();i++){
            char c = text.charAt(i);
            if(al.contains(c)){
                if(hp.containsKey(c)){
                    hp.put(c,hp.get(c)+1);
                }else{
                    hp.put(c,1);
                }
            }
        }
         for(int i=0;i<al.size();i++){
             if(!hp.containsKey(al.get(i))) return 0;
         }
               int min=Integer.MAX_VALUE;
               for(char x: hp.keySet()){
                 if(x=='l' || x=='o') min = Math.min(min,hp.get(x)/2);
                 else min = Math.min(min,hp.get(x));
               }
               for(char x: hp.keySet()){
                   if(hp.get(x)<min) return 0;
               }
        return min;
}
               }
