class Solution {
public:
    int maxNumberOfBalloons(string text) {
        //if length of string will be less than 7 letters then it will be impossible to make the string "balloon" .
        if(text.length() < 7){return 0 ;}

       // taking a empty array to store occurance of letters 
        int a[5] = {0,0,0,0,0} ;
        //checking for each alphabet if present 
        for(char chars : text){
            if(chars == 'b' ){
                a[0]++ ;
            }
            if(chars == 'a' ){
                a[1]++ ;
            }
            if(chars == 'l' ){
                a[2]++ ;
            }
            if(chars == 'o' ){
                a[3]++ ;
            }
            if(chars == 'n' ){
                a[4]++ ;
            }

        }
        //taking a integer variable answer to store the answer
        int answer = INT_MAX ;

        // checking occurence of letters 'l' and 'o'
        a[2] = int(a[2]/2) ; 
        a[3] = int(a[3]/2) ;

        //checking the number of time 'balloon' occurs
        for(int i = 0 ; i < 5 ; i++ ){
            if(answer > a[i]){
                answer = a[i] ;
            }
        }
        return answer;
    }
};

