class Solution {

    public int maxNumberOfBalloons(String text){

        //you can replace any string to be searched here or pass as parameter
        String str = "balloon";

        Map<Character, Integer> map = new HashMap<>();

        //populate only search string chars from text string
        for (int i = 0; i < text.length(); i++) {
            char ch = text.charAt(i);
            if (str.indexOf(ch) >= 0) {
                map.put(ch, map.getOrDefault(ch, 0) + 1);
            }
        }

        //populate map for searchString to require get frequency of each char
        Map<Character, Integer> strMap = new HashMap<>();
        for (int i = 0; i < str.length(); i++) {
            char ch = str.charAt(i);
            strMap.put(ch, strMap.getOrDefault(ch, 0) + 1);
        }

        int min = Integer.MAX_VALUE;

        //find min words can be made from text string chars
        for (Map.Entry<Character, Integer> entry : strMap.entrySet()) {

            char key = entry.getKey();
            int value = entry.getValue();

            if (!map.containsKey(key))
                return 0;

            int occurValue = map.get(key);

            if (occurValue / value < min) {
                min = occurValue / value;
            }

        }
        return min;
    }

}
