class Solution {
public:
    string ans = "";
    long long int get(int a,int b){
        long long int ans = 1;
        int k = a+b;
        while(k>a){
            ans *= k;
            k--;
            while(b>1&&ans%b==0){
                ans /= b;
                b--;
            }
        }
        return ans;
    }
    void solve(int a,int b,int k,string s){
        if(a<0||b<0)return ;
        if(a==0&&b==0)ans = s;
        long long int x = get(a,b-1);
        if(k==1){
            if(a==0){
                solve(a,b-1,k,s+'H');
            }else if(b==0){
                solve(a-1,b,k,s+'V');
            }
        }
        if(k*1LL>x){
            solve(a-1,b,k-x,s+'V');
        }else{
            solve(a,b-1,k,s+'H');
        }
    }
    string kthSmallestPath(vector<int>& v, int k) {
        solve(v[0],v[1],k,"");
        return ans;
    }
};
