class Solution {
public:
    int getCount(int h, int v)
    {
        if(h == 0 || v == 0) return 1;
        long long res = 1;
        if(h < v) swap(h, v);
        for(int i = h + 1; i <= (h+v); i++) res *= i;
        for(int i = 1; i <= v; i++) res /= i;
        return (int)res;
    }
