                v--;
                res.push_back('V');
                continue;
            }
            if(v == 0) 
            {
                h--;
                res.push_back('H');
                continue;
            }
            int cnt = getCount(h - 1, v);
            if(cnt >= k)
            {
                res.push_back('H');
                h--;
            }
            else
            {
                k -= cnt;
                v--;
                res.push_back('V');
