    #define ll long long

    ll dp[31][31];

    void helper(int n){
        dp[0][0] = 1;
        for(int i = 1; i <= n; i++){
            for(int j = 0; j <= i; j++){
                if(j == 0 || j == i) dp[i][j] = 1;
                else dp[i][j] = (dp[i-1][j] + dp[i-1][j-1]) % mod;
