int n,m;
int cache[20][20];
string res;
int dp(int x,int y){
    int &ans = cache[x][y];
    if(x > n or y > m)
        return ans = 0;
    if(x == n and y == m)
        return ans = 1;
    if(ans != -1)
        return ans;
    ans = dp(x,y+1) + dp(x+1,y); 
    return ans;
}
void dp1(int x,int y,int k)
{
    if(x > n or y > m)
        return;
    if(x == n and y == m)
        return;
    int &ans = cache[x][y];
    int l = cache[x][y+1];
    if(l >= k){
        res += 'H';
        dp1(x,y+1,k);
        return;
    }
    res += 'V';
    dp1(x+1,y,k-l);
}

class Solution {
public:
    string kthSmallestPath(vector<int>& d, int k) {
        n = d[0];
        m = d[1];
        memset(cache,-1,sizeof(cache));
        dp(0,0);
        res = "";
        dp1(0,0,k);

        return res;
    }
};
