from math import factorial
class Solution:
    def perm(self, destination) -> int:
        return factorial(destination[1]+destination[0]) // factorial(destination[0]) // factorial(destination[1])
    def kthSmallestPath(self, destination: List[int], k: int) -> str:
        if k==0:
            return 'H' * destination[1] + 'V' * destination[0]
        if destination[1]==0:
            return 'V' * destination[0]
        ans = '' 
        while k-1>=self.perm((destination[0], destination[1]-1)) and destination[0]>0:
            k = k - self.perm((destination[0], destination[1]-1))
            destination = destination[0]-1, destination[1]
            ans = ans + 'V'
        if destination[0]==0:
            return ans + 'H' * destination[1]
        if k-1<self.perm((destination[0], destination[1]-1)):
            return ans + 'H' + self.kthSmallestPath((destination[0], destination[1]-1), k)
        return ans


