class Solution {
    public static String kthSmallestPath(int[] destination, int k) {
        int rows = destination[0] + 1;
        int cols = destination[1] + 1;

        int[][] dp = new int[rows][cols];

        // Calculate the number of possible paths for each cell
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (i == 0 || j == 0) {
                    dp[i][j] = 1;
                } else {
                    dp[i][j] = dp[i - 1][j] + dp[i][j - 1];
                }
            }
        }

        StringBuilder sb = new StringBuilder();

        int remainingH = cols - 1;  // Number of remaining horizontal moves
        int remainingV = rows - 1;  // Number of remaining vertical moves

        for (int i = 0; i < rows + cols - 2; i++) {
            if (remainingH > 0 && dp[remainingV][remainingH - 1] >= k) {
                sb.append("H");
                remainingH--;
            } else {
                sb.append("V");
                if (remainingH > 0) {
                    k -= dp[remainingV][remainingH - 1];
                }
                remainingV--;
            }
        }

        return sb.toString();

    }
    /*
    private static void backtrack(List<String> paths, String currentPath, int row, int col, int rows, int columns) {
        if (row == rows - 1 && col == columns - 1) {
            paths.add(currentPath);
            return;
        }

        if (row < rows - 1) {
            backtrack(paths, currentPath + "V", row + 1, col, rows, columns);
        }

        if (col < columns - 1) {
            backtrack(paths, currentPath + "H", row, col + 1, rows, columns);
        }
    }*/
}
