class Solution:
    def kthSmallestPath(self, destination, k):
        i,j = destination

        @lru_cache(None)
        def dfs(i,j,k):
            if k == 1:
                return "H"*j + "V"*i
            else:
                val = math.comb(i+j-1,j-1)

                if k <= val:
                    return "H" + dfs(i,j-1,k)
                else:
                    return "V" + dfs(i-1,j,k-val)

        return dfs(i,j,k)






