class Solution:
    def kthSmallestPath(self, destination: List[int], k: int) -> str:
        # Returns the Kth smallest string containing h H's and v V's.
        def recurse(h, v, k):
            # If k is 1, return the smallest lexicographical string.
            if k == 1:
                return 'H' * h + 'V' * v

            # The first character will be 'V' if k exceed the number of
            # combinations starting with an 'H'.
            hCombinations = comb(h + v - 1, v)
            if hCombinations < k:
                return 'V' + recurse(h, v - 1, k - hCombinations)

			# If not, the next character will be an 'H'.
            return 'H' + recurse(h - 1, v, k)

        return recurse(destination[1], destination[0], k)
