class Solution {
public:
    long long fac[32];
    int mod=1000000007;
    void pre()
    {
        fac[0]=1;
        for(long long i=1;i<=30;++i)
        {
            fac[i]=(fac[i-1]*i)%mod;
        }
    }
    long long power(long long a,int b)
    {
        if(b==0)return 1;
        long long p=power(a,b/2);
        p=(p*p)%mod;
        if(b&1)
        {
            p=(p*a)%mod;
        }
        return p;
    }
    long long modInverse(long long a)
    {
        return power(a,mod-2)%mod;
    }
    long long nCr(int n,int r)
    {
        if(n<r)return 0;
        if(r==0)return 1;

        return ((((fac[n]%mod)*(modInverse(fac[r]%mod)%mod))%mod)*(modInverse(fac[n-r]%mod)%mod))%mod;
    }
    string kthSmallestPath(vector<int>& destination, int k) {
        pre();
        int h=destination[0],w=destination[1];
        string res="";
        while(h>0&&w>0)
        {
            long long pos=nCr(h+w-1,w-1);
            //cout<<pos<<" ";
            if(pos<k)
