	class Solution {
	public:
		int comb(int n, int r){
			if(r>n)return 0;
			int res=1;
			for(int i=1;i<=r;i++)res=res*(n-i+1)/i;
			return res;
		}
		string kthSmallestPath(vector<int>& destination, int k) {
			int V=destination[0];        
			int H=destination[1];
			if(V==0 || H==0 || k==1){
				string s="";
				for(int i=0;i<H;i++)s.push_back('H');            
				for(int i=0;i<V;i++)s.push_back('V');
				return s;
			}
			int count = comb(V+H-1,V);
			if (count >= k) {
				destination[1]-=1;
				return "H" + kthSmallestPath(destination,k);
			}
			destination[0]-=1;
			return "V" + kthSmallestPath(destination,k-count);
		}
	};

