class Solution {

    public String kthSmallestPath(int[] destination, int k) {

        int m = destination[0];
        int n = destination[1];

        int dp[][] = new int[m+1][n+1];
        //first we need to figer out how many possible way's are there to reach the destination
        for(int i = m; i >= 0; i--)
        {
            for(int j = n; j >= 0 ; j--)
            {
                if(i == m && j == n ) dp[i][j] = 1;
                else if( i == m) dp[i][j] = dp[i][j+1];
                else if(j == n) dp[i][j] = dp[i+1][j];
                else dp[i][j] = dp[i+1][j] + dp[i][j+1];
            }
        }

        StringBuilder sb = new StringBuilder();
        //now one thing we have to note here that
        // k = 1 then there is fixed path
        //k = 2 then H(right direction) will reduce
        // k = destination then start with v
        //so why not we move such a way
        helper(0, 0, k, sb, dp);
        return sb.toString();

    }

    //In helper funtion what we are doing we just travelling according to dp see below
    public void helper(int i, int j, int k, StringBuilder sb, int dp[][]){


