        string s;
        for (int i = 0; i < N; ++i) {
            if (h) { // we have H available to pick
                int c = comb(h - 1 + v, v); // number of combinations
                if (k <= c) s.push_back('H'), h--; // pick H as k in within range
                else s.push_back('V'), v--, k -= c; // otherwise pick V
            } else s.push_back('V');
        }
        return s;
    }
