class Solution {
    public String kthSmallestPath(int[] d, int k) {
        int h = d[1], v = d[0];
        int[][] dp = new int[h+v+1][v+1];
        dp[0][0] = 1;
        for(int i = 1 ; i < dp.length ; i++){
            dp[i][0] = 1;
            for(int j = 1 ; j <= v ; j++){
                dp[i][j] += dp[i-1][j] + dp[i-1][j-1];
            }
        }
        char[] ret = new char[h+v];
        int vt = v;
        int idx = h+v;
        while(idx > 0 && k > 0 && vt > 0){
            while(idx >= 0  && dp[idx-1][vt] >= k){
                idx--;
            }
            if(k == dp[idx][vt]){
                while(idx > 0 && vt > 0){
                    ret[h+v-idx] = 'V';
                    idx--;
                    vt--;
                }
                break;
            }else{
                k -= dp[idx-1][vt];
                ret[h+v-idx]='V';
                vt--;
            }
        }
        for(int i = 0 ; i < ret.length ; i++){
            if(ret[i] != 'V'){
                ret[i] = 'H';
            }
        }
        return new String(ret);
    }
}
