class Solution {
    public String kthSmallestPath(int[] destination, int k) {
        StringBuilder b = new StringBuilder();
        helper(destination[0], destination[1], k, b);
        return b.toString();
    }

    void helper(int row, int col, int k, StringBuilder b) {
        if(k == 1 || row == 0 || col == 0) {
            for(int i = 0; i < col; i++) {
                b.append('H');
            }
            for(int i = 0; i < row; i++) {
                b.append('V');
            }
            return;
        }
        int add = C(row + col - 1, row);
        if(k <= add) {
            b.append('H');
            helper(row, col - 1, k, b);
        }
        else {
            b.append('V');
            helper(row - 1, col, k - add, b);
        }
    }

    int C(int n, int k) {
        double res = 1;
        for (int i = 1; i <= k; ++i)
            res = res * (n - k + i) / i;
        return (int)(res + 0.01);
    }
}
