class Solution {
public:
	string kthSmallestPath(vector<int>& arr, int k) {
		const int n = arr[0], m = arr[1];
		long long x = m;
		for(int i = m + 1; i <= n + m; i++)
			x = x * i / (i - m);
		string ans(n + m, 'V');
		for(int i = 0, a = m, b = n; i < n + m; i++) {
			x /= a + b;
			if(a && k <= x)
				ans[i] = 'H', x *= --a;
			else
				k -= x, x *= b--;
		}
		return move(ans);
	}
};
