class Solution:
    def kthSmallestPath(self, destination: List[int], k: int) -> str:
        ans = ""
        R, C = destination

        while R + C > 0:
            child_cnt = math.comb(R + C - 1, R)
            if child_cnt >= k :
                ans += "H"
                C -= 1
            else:
                ans += "V"
                R -= 1
                k -= child_cnt

