class Solution:
    def kthSmallestPath(self, destination: List[int], k: int) -> str:
        # start from HHH...VVV, we then iteratively put V forward and 
        # check if the number of combinations would exceed k

        V, H = destination

        def cal_num_comb(V, H):
            ans1, ans2 = 1, 1
            for i in range(V):
                ans1 *= (H+V-i)
                ans2 *= (V-i)
            return ans1 / ans2

        def helper(V, H, k):
            if k == 1: return 'H'*H + 'V'*V
            k -= 1
            for i in range(1, H+1):
                num_comb = cal_num_comb(V-1, i)
                if num_comb < k:
                    k -= num_comb
                else:
                    return 'H'*(H-i)+'V' + helper(V-1, i, k)

        return helper(V, H, k)



